<?php

/**
 * Constants interface holding read-only configuration-defaults
 * specific to the Ilios instance at University of California, San Francisco (UCSF).
 */
interface Ilios_Config_Ucsf
{
	/**
	 * @var int Ilios-internal identifier for School of Medicine
	 */
	const SCHOOL_OF_MEDICINE_ID = 1;
	/**
	 * @var int Ilios-internal identifier for School of Dentistry
	 */
	const SCHOOL_OF_DENTISTRY_ID = 2;
	/**
	 * @var int Ilios-internal identifier for School of Pharmacy
	 */
	const SCHOOL_OF_PHARMACY_ID = 3;
	/**
	 * @var int Ilios-internal identifier for School of Nursing
	 */
	const SCHOOL_OF_NURSING_ID = 4;
	/**
	 * @var int Ilios-internal identifier for "other" schools
	 */
	const SCHOOL_OTHER_ID = 5;

}
